document.addEventListener("DOMContentLoaded",()=>{const form=document.querySelector("form");if(!form)return;form.addEventListener("submit",e=>{const nome=document.getElementById("nome");const email=document.getElementById("email");let valido=!0;if(!nome||nome.value.trim()===""){alert("Nome é obrigatório");valido=!1}
if(!email||!email.value.includes("@")){alert("Email inválido");valido=!1}
if(!valido)e.preventDefault();})})